							{/*				<div className="title">{activity.title}</div>*/}
							{/*				<div className="date">{new_date}</div>*/}
							{/*				<Link to="#">View more</Link></div>*/}
							{/*		</div>);*/}
							{/*})}*/}
						</div>
					</div>
					<div className="create-icon">
						<FontAwesomeIcon onClick={this.togglePopUp} icon={faPlusCircle} />
					</div>
				</div>
			</div>
		);
	}
}

